class Config:    @staticmethod    def get_default_config(args):        config = Config(args)        return config    @staticmethod    def get_dev_config(args):        config = Config(args)        config.NUM_EPOCHS = 1        config.BATCH_SIZE = 1        return config    def __init__(self):        # PyTorch setting        self.USE_CUDA = True        self.SAVE_PATH = None        self.LOAD_PATH = None        # Training parameters        self.NUM_EPOCHS = 10        self.BATCH_SIZE = 256        # Model parameters        self.ENC_DROPOUT = 0.1        self.ENC_LAYERS = 1        self.DEC_DROPOUT = 0.1        self.DEC_LAYERS = 1        self.DEC_MAX_LEN = 100        self.EMBED_SIZE = 128        self.HIDDEN_SIZE = 2 * 128 # bidirectional embedding        self.ATTENTION = True        # TODO: add arguments parser for extra parameters (paths etc.)